      q:text
    })
    console.log(params,"hii")
    setLoading(true)
    const response= await fetch(`http://api.weatherstack.com/current?access_key=c2135e43421bf7bfad596b19692fbea0&query=${params}`,{

    })
    const data=await response.json()
    setWeather(data);
    console.log("Fetched")
    setLoading(false)

 }
    return <weather_context.Provider value={{weather,loading,fetchdata}}>
  {children}
    </weather_context.Provider>;
}

export default Context
